package operators;

public class LogicalOperators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		// Logical operators
				//   && , ||
				int a = 2;
				int b = 5;
				
				boolean f1 = true;
				boolean f2 = false;
				
				boolean f3= f1||f2;
				System.out.println(f3);
				
				boolean f4= f1&&f2;
				System.out.println(f4);
				
				boolean f5= f1&f2;
				System.out.println(f5);
				
	}

}
